import { useState } from "react";
import Banner from "./componentes/Banner";
import Formulario from "./componentes/Formulario";
import Rodape from "./componentes/Rodape";
import Time from "./componentes/Time";
import { v4 as uuidv4 } from 'uuid';

function App() {

  const [times, setTimes] = useState([
    {
      id: uuidv4(),
      nome: 'Chapéus de Palha',
      cor: '#57C278'
    },
    {
      id: uuidv4(),
      nome: 'Piratas do Ruivo',
      cor: '#82CFFA'
    },
    {
      id: uuidv4(),
      nome: 'Os Shichibukai',
      cor: '#A6D157'
    },
  ]);

  const inicial = [
    {
      id: uuidv4(),
      favorito: false,
      nome: 'MONKEY D. LUFFY',
      cargo: 'Capitão',
      imagem: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRBk8sHgzjC-ei-LLTrT-q7aaeKbdeRxavsePFsPvbapIphKLbl4RAMbUh_rBvNhMJGFeU&usqp=CAU',
      time: times[0].nome
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: 'RORONOA ZORO',
      cargo: 'Combatente',
      imagem: 'data:image/jpeg;base64,',
      time: times[0].nome
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: 'VISMONKE SANJI',
      cargo: 'Cozinheiro',
      imagem: 'https://i.pinimg.com/736x/69/ab/56/69ab56408414b92672140c608e6d7504.jpg',
      time: times[0].nome
    },
    {
      id: uuidv4(),
      nome: 'Nami',
      cargo: 'Navegadora',
      imagem: 'https://i.pinimg.com/736x/f3/c6/7a/f3c67a895875a39527ef89bfd3d422e8--one-piece-nami-manga-anime.jpg',
      time: times[0].nome
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: 'SHANKS',
      cargo: 'Capitão',
      imagem: 'https://i.pinimg.com/736x/ac/73/e0/ac73e0a96f5ef3bbabde03533df4dd7d.jpg',
      time: times[1].nome
    },
    {
      id: uuidv4(),
      nome: 'BENN BECKMAN',
      cargo: 'Imediato',
      imagem: 'https://i.pinimg.com/originals/08/a3/20/08a320142acb69036c517ed314e12baa.jpg',
      time: times[1].nome
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: 'LUCKY ROUX',
      cargo: 'Combatente',
      imagem: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSqjaIztAyhNm0vQNTXLUk7iR7d7dmNa-pkrw&usqp=CAU',
      time: times[1].nome
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: 'YASSOP',
      cargo: 'Atirador',
      imagem: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS7SyYz-tZODJVoj7JxVSlCRAIFQU9ZfhH5ZQ&usqp=CAU',
      time: times[1].nome
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: 'DRACULE MIHAWK',
      cargo: 'Melhor Espadachim do Mundo',
      imagem: 'data:image/jpeg;base64,',
      time: times[2].nome
    },
    {
      id: uuidv4(),
      nome: 'DONQUIXOTE DOFLAMINGO',
      cargo: 'Dêmonio Celestial',
      imagem: 'https://i.pinimg.com/474x/5c/51/f4/5c51f478708eb2fcc4170d981c10d680.jpg',
      time: times[2].nome
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: 'CROCODILE',
      cargo: 'Rei do deserto',
      imagem: 'data:image/jpeg;base64,',
      time: times[2].nome
    },
    {
      id: uuidv4(),
      nome: 'TRAFALGAR LAW',
      cargo: 'Cirurgião da morte',
      imagem: 'https://i.pinimg.com/originals/68/eb/e4/68ebe48ee3c663d6f839f9444de0fe40.jpg',
      time: times[2].nome
    },
  ]

  const [colaboradores, setColaboradores] = useState(inicial)

  function deletarColaborador(id) {
    setColaboradores(colaboradores.filter(colaborador => colaborador.id !== id));
  }

  function mudarCor(cor, id) {
    setTimes(times.map(time => {
      if(time.id === id) {
        time.cor = cor;
      }
      return time;
    }));
  }

  function cadastrarTime({ nome, cor }) {
    setTimes([...times, { nome, cor, id: uuidv4() }])
  }

  function resolverFavorito(id) {
    setColaboradores(colaboradores.map(colaborador => {
      if(colaborador.id === id) colaborador.favorito = !colaborador.favorito;
      return colaborador;
    }))
  }


  return (
    <div>
      <Banner />
      <Formulario aoCriarTime={cadastrarTime} times={times.map(time => time.nome)} aoCadastrar={colaborador => setColaboradores([...colaboradores, colaborador])} />
      <section className="times">
        <h1>Minha organização</h1>
        {times.map((time, indice) => <Time mudarCor={mudarCor} key={indice} time={time} colaboradores={colaboradores.filter(colaborador => colaborador.time === time.nome)} aoDeletar={deletarColaborador} aoFavoritar={resolverFavorito} />)}
      </section>
      <Rodape />
    </div>
  );
}

export default App;